import React from 'react';
import TopNav from "../TopNav";
import Footer from "../Footer";


export class Students extends React.Component {
    render() {
        return (
            <div>
                {this.props.student.name}
            </div>
        )
    }
}

export default class StudentsPage extends React.Component {
    constructor(props) {
        //必須
        super(props);

        //初期化
        this.state = {
            years: [2000, 2001, 2002, 2003, 2004],
            studentsInfo: []
        };

    }

    componentDidMount() {
        this.setState({
            studentsInfo: [
                {
                    year: 2000,
                    students: [
                        {name: 'Gerlee', college: 'Sendai'},
                        {name: 'Chimgee', college: 'Toyota'}
                    ]
                },
                {
                    year: 2012,
                    students: [
                        {name: 'Amraa', college: 'Sendai'},
                        {name: 'Eegii', college: 'Toyota'}
                    ]
                },
            ]
        })
    }

    render() {

        return (
            <div>
                <TopNav/>
                <div className="bodyContainer">
                    <ul className="collapsible">
                        {this.state.studentsInfo.map((info, index) => (
                            <li key={index}>
                                <div className="collapsible-header">
                                    <i className="material-icons">group</i>
                                    {info.year} onii elsegchid
                                </div>
                                <div className="collapsible-body">
                                        <Students
                                            key={index}
                                            students={info.students}
                                        />
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
                <Footer/>
            </div>
        )
    }
}
