import React from 'react';
import TopNav from "../TopNav";
import M from "materialize-css";

export default class AboutPage extends React.Component {
    componentDidMount() {
        M.AutoInit();

        let carousel = document.querySelectorAll('.carousel.carousel-slider');

        this.setState({news: [1, 2, 3]});

        M.Carousel.init(carousel, {
            indicators: true,
            fullWidth: true
        });
    }

    render() {
        return (
            <div>
                <TopNav/>
                <div className="bodyContainer">
                    <div className="title">Kosen Club</div>
                    <div>
                        1990 оноос Японы засгийн газрын шугамаар , Японы технологийн сургуульд Монгол оюутан суралцаж эхэлжээ.
                        Жилээс жилд суралцагчдын тоо өсөн нэмэгдсээр, эдүгээ 200 илүү суралцагчид болон төгсөгчидтэй “Коосэн”-ийхан гэдэг нэгэн өргөн арми бий болсон байна.
                        “Коосэн”-гийхан бид бүхэн коосэн, цаашлаад их сургуулиар дамжин, бодит капиталыг бий болгох технологийг эзэмшин, нийгмийн сайн сайхны төлөө бүтээн байгуулалтыг хийх “инженерүүд” болдог билээ.
                        “Коосэн” клуб нь , “Коосэн”-ийнхан , “Инженерүүд” гэсэн бидний холбоог үндэслэн , өөрсдийн ашиг сонирхолыг илэрхийлэн, бие биенээ тусалж дэмжиж цаашлаад Монгол Японыг холбосон гүүр үүсгэн
                        ажиллах зорилготой юм. Тус клуб нь сайн дурын хэт албархаг бус , ашгийн бус байгууллага байх болно.
                    </div>
                </div>
            </div>
        )
    }
}
