import React from 'react';
import TopNav from "../TopNav";
import M from "materialize-css";
import newsImage1 from '../images/icon/about.png';

export class NewsCard extends React.Component {
    render() {
        const title = this.props.title;
        return (
            <div className="col cardColumn">
                <div className="card">
                    <div className="card-image">
                        <img src={newsImage1}/>
                        <span className="card-title blue-text">{title}</span>
                    </div>
                    <div className="card-content">
                        {this.props.children}
                    </div>
                </div>
            </div>
        )
    }
}

export class TopImages extends React.Component {
    render() {
        return (
            <div className="carousel carousel-slider center">
